feed:
  {%- unless site.feed.hide_generator %}
  Jekyll
  {%- endunless %}

  {%- if page.url == site.feed.path -%}
    {%- assign feed_title = site.theme_settings.category_feed_all_name %}
  {%- else -%}
    {%- assign feed_title = site.data.categories[page.category].title -%}
  {%- endif %}

  {{ site.time | date_to_xmlschema }}
  {{ page.url | absolute_url | xml_escape }}
  {%- assign icon = site.feed.icon | default: site.favicon -%}
  {%- if icon %}
  {{ '/assets/icons/' | append: icon | absolute_url | xml_escape }}
  {%- endif -%}
  {%- assign title = site.title | default: site.name -%}
  {%- assign title_separator = site.feed.title_separator | default: ': ' -%}
  {%- if page.collection and page.collection != 'posts' -%}
    {%- assign collection = page.collection | replace: '-', ' ' | titleize -%}
    {%- assign title = title | append: title_separator | append: collection -%}
  {%- endif -%}
  {%- comment -%}
  {%- if page.category -%}
    {%- assign category = site.data.categories[page.category].title -%}
    {%- assign title = title | append: title_separator | append: category -%}
  {%- endif -%}
  {%- endcomment -%}
  {%- if title %}
  {{ title | smartify | strip_html | normalize_whitespace }}{% if feed_title != '' %}{{ site.feed.title_separator }}{% endif %}{{ feed_title }}
  {%- endif -%}
  {%- if page.description or site.description %}
  {{ page.description | default: site.description | smartify | strip_html | normalize_whitespace }}
  {%- endif -%}
  {%- if site.author %}

    {{ site.author.name | default: site.author | xml_escape }}
    {%- if site.author.email %}
    {{ site.author.email | xml_escape }}
    {%- endif -%}
    {%- if site.author.uri %}
    {{ site.author.uri | xml_escape }}
    {%- endif %}

  {%- endif -%}
  {%- if site.author.copyright %}
  {{ site.author.copyright | xml_escape }}
  {%- endif -%}
  {%- if page.url == site.feed.path -%}
    {%- assign posts = site.posts -%}
  {%- elsif page.collection -%}
    {%- assign posts = site[page.collection] -%}
  {%- else -%}
    {%- assign posts = site.posts | where: 'categories', page.category -%}
  {%- endif -%}
  {%- unless site.show_drafts -%}
    {%- assign posts = posts | where_exp: 'post', 'post.draft != true' -%}
  {%- endunless -%}
  {%- assign posts = posts | sort: 'date' | reverse -%}
  {%- assign posts_limit = site.feed.posts_limit | default: 10 -%}
  {%- for post in posts limit: posts_limit %}

    {%- assign post_title = post.title | smartify | strip_html | normalize_whitespace %}
    {{ post_title }}
    {%- assign post_url_absolute_escaped = post.url | absolute_url | xml_escape %}

    {{ post.date | date_to_xmlschema }}
    {{ post.last_modified_at | default: post.date | date_to_xmlschema }}
    {%- if post.uuid %}
    urn:uuid:{{ post.uuid }}
    {%- else %}
    {{ post.id | absolute_url | xml_escape }}
    {%- endif -%}
    {%- assign post_author = post.author | default: post.authors[0] | default: site.author -%}
    {%- assign post_author = site.data.authors[post_author] | default: post_author -%}
    {%- assign post_author_email = post_author.email | default: nil -%}
    {%- assign post_author_uri = post_author.uri | default: nil -%}
    {%- assign post_author_name = post_author.name | default: post_author %}

      {{ post_author_name | default: '' | xml_escape }}
      {%- if post_author_email %}
      {{ post_author_email | xml_escape }}
      {%- endif -%}
      {%- if post_author_uri %}
      {{ post_author_uri | xml_escape }}
      {%- endif %}

    {%- if post.category %}

    {%- elsif post.categories -%}
      {%- for category in post.categories %}

      {%- endfor -%}
    {%- endif %}
    {%- for tag in post.tags -%}
      {%- unless post.categories contains tag %}

      {%- endunless -%}
    {%- endfor -%}
    {%- assign post_image = post.featured_image.path | default: post.featured_image -%}
    {%- if post_image -%}
      {%- unless post_image contains '://' -%}
        {%- assign post_image = post_image | absolute_url -%}
      {%- endunless %}


    {%- endif -%}
    {%- assign post_summary = post.description | default: post.excerpt -%}
    {%- if post_summary and post_summary != empty %}
      {%- if site.feed.smartify -%}
        {%- assign post_summary = post_summary | smartify -%}
      {%- endif %}
    {{ post_summary | strip_html | normalize_whitespace }}
    {%- endif -%}
    {%- assign excerpt_only = post.feed.excerpt_only | default: site.feed.excerpt_only -%}
    {%- if excerpt_only -%}
      {%- assign post_content = '
' | append: post_summary | append: '' %}
    {%- else %}
      {%- if site.feed.smartify %}
        {%- assign post_content = post.content | smartify %}
      {%- else -%}
        {%- assign post_content = post.content %}
      {%- endif -%}
      {%- assign post_content = '
' | append: post_summary | append: '' | append: post_content %}
    {%- endif %}
    {{ post_content | strip }}

  {%- endfor %}
